import 'vue3-openlayers';
import { inject, ref } from 'vue'; //https://vue3openlayers.netlify.app/

export default {
  name: "bird-vue-map",
  props: ['lon', 'lat', 'index'],
  methods: {
    zoomChanged(currentZoom) {
      this.currentZoom = currentZoom;
    },

    centerChanged(center) {
      this.currentCenter = center;
    }

  },

  mounted() {
    this.center = [this.lon, this.lat];
    this.coordinate = [this.lon, this.lat];
  },

  setup() {
    const center = ref([0, 0]);
    const projection = ref('EPSG:4326');
    const zoom = ref(4);
    const rotation = ref(0);
    const view = ref(null);
    const map = ref(null);
    const radius = ref(10);
    const strokeWidth = ref(10);
    const strokeColor = ref('red');
    const fillColor = ref('white');
    const coordinate = ref([0, 0]);

    const pointer = require("@/assets/pin.png");

    const url = ref("https://openlayers.org/en/latest/examples/data/geojson/countries.geojson");
    const format = inject('ol-format');
    console.log(format);
    const geoJson = new format.GeoJSON();
    return {
      center,
      projection,
      zoom,
      rotation,
      map,
      view,
      pointer,
      url,
      geoJson,
      radius,
      strokeWidth,
      strokeColor,
      fillColor,
      coordinate
    };
  },

  data() {
    return {
      currentCenter: this.center,
      currentZoom: this.zoom
    };
  }

};
